import React, { useState, useEffect } from 'react';
import { Button, View, Image, Platform } from 'react-native';
import { Asset } from 'expo-asset';
import * as ImageManipulator from 'expo-image-manipulator';
import * as ImagePicker from 'expo-image-picker';


export default function App() {
  const [width, setWidth] = useState(700);
  const [height, setHeight] = useState(500);

  const [image, setImage] = useState(null);

  useEffect(() => {



    (async () => {

      if (Platform.OS !== 'web') {
        const { status } = await ImagePicker.requestMediaLibraryPermissionsAsync();
        if (status !== 'granted') {
          alert('Sorry, we need camera roll permissions to make this work!');
        }
        setReady(true);
      }
    })();
  }, []);

  const flip = async () => {
    const manipResult = await ImageManipulator.manipulateAsync(
      image.localUri || image.uri,
      [ { flip: ImageManipulator.FlipType.Horizontal }],
      { compress: 1, format: ImageManipulator.SaveFormat.PNG }
    );
    setImage(manipResult);
  };

  const flipY = async () => {
    const manipResult = await ImageManipulator.manipulateAsync(
      image.localUri || image.uri,
      [ { flip: ImageManipulator.FlipType.Vertical }],
      { compress: 1, format: ImageManipulator.SaveFormat.PNG }
    );
    setImage(manipResult);
  };

  const rotate = async () => {
    const manipResult = await ImageManipulator.manipulateAsync(
      image.localUri || image.uri,
      [ { rotate: 90 } ],
      { compress: 1, format: ImageManipulator.SaveFormat.PNG }
    );
    setImage(manipResult);
  };

  const resize = async () => {
    let x = width/2;
    const manipResult = await ImageManipulator.manipulateAsync(
      image.localUri || image.uri,
      [ { resize: {width: 90}}],
      { compress: 1, format: ImageManipulator.SaveFormat.PNG }
    );
    setImage(manipResult);
  };

  const pickImage = async () => {
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.All,
      allowsEditing: true,
      aspect: [4, 3],
      quality: 1,
    });

    if (!result.cancelled) {
      setImage(result);
    }
  };


  return (
    <View style={{ flex: 1, justifyContent: 'center' }}>
      <Button title="Pick an image from camera roll" onPress={pickImage} />
      {image && <Image source={{ uri: image.uri }} style={{ width: width, height: height }} />}
      <Button title="Flip Horizontal" onPress={flip} />
      <Button title="Flip Vertical" onPress={flipY} />
      <Button title="Rotate 90" onPress={rotate} />
      <Button title="Resize 50%" onPress={resize} />


    </View>
  );
}





















