import React , {useEffect, useState} from 'react';
import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View } from 'react-native';
import * as Location from 'expo-location'

export default function App() {

  let [latitude,setLatitude] = useState();
  let [longitude,setLongitude] = useState();

  useEffect(()=> {
    ( async ()=>{
      //app asks user for permission to access location
      //status: 'granted' (string)/ when user accepts
      let {status} = await Location.requestForegroundPermissionsAsync();
      if(status==="granted"){

      //gets object of coordinates : {coords: {â€¦}, timestamp: 1626139271918}
      let locationData = await Location.getCurrentPositionAsync();
      setLongitude(locationData.coords.longitude);
      setLatitude(locationData.coords.latitude);

    }
    })();//self invoked function
  },[])

  return (
    <View style={styles.container}>
      <Text>{longitude}</Text>
      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'pink',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
